import './UserRegister.scss'
import React, {Component, Fragment, createRef} from 'react'
import Main from '../templates/Main'
import axios from 'axios'
import Toast from '../utils/Toast';
import baseUrl from '../../config/baseUrl'

const baseUrlBackend = `${baseUrl.baseUrlBackend}`

const headerProps = {
    icon: 'sign-in',
    title: 'Registrar',
    subtitle: 'Você precisa estar logado para acessar o sistema.',
}

export default class UserRegister extends Component {
    constructor(props) {
        super(props)

        this.child = createRef()

        this.state = {
            name: props.name || '',
            username: props.username || '',
            password: props.password || '',
            repeat_password:'',
            toastMessage:'false',
            errorForm:[],
            formRegister:{
                name:{
                    label:"Nome",
                    inputName:"name",
                    maxLength:85
                },
                password:{
                    label:"Senha",
                    inputName:"password",
                    maxLength:150
                },
                submitButton:"Cadastrar"

            }
        }
    }

    isValidUser() {
        const {name, username, password, repeat_password} = this.state

        const validate = {
            errors:[]
        }

        if (!name || name == '') {
            validate.errors.push('O nome precisa ser preenchido.');
        }
        if (name && name != '') {
            if(name.length < 3 || name.length > 85) {
                validate.errors.push('O nome precisa ter de 3-85 caracteres.');
            }
        }
        if (!username || username == '') {
            validate.errors.push('O nome de usuário precisa ser preenchido.');
        }
        if (username && username != '') {
            if (username.length < 4 || username.length > 20) {
                validate.errors.push('O usuário precisa ter de 4-20 caracteres.');
            }
        }
        if (!password || password == '') {
            validate.errors.push('A senha precisa ser preenchida.');
        }
        if (!repeat_password || repeat_password == '') {
            validate.errors.push('O campo repita a senha precisa ser preenchido.');
        }
        if (password && password != '') {
            if (password.length < 8) {
                validate.errors.push('A senha precisa ser ao menos 8 caracteres.');
            }
            if (password.length > 149) {
                validate.errors.push('A senha precisa ser menor que 150 caracteres.');
            }
        }
        if (repeat_password && repeat_password != '' && password && password != '') {
            if (password !== repeat_password) {
               validate.errors.push('As senhas digitadas precisam ser iguais');
            }
        }
        if (validate.errors.length > 0) {
            this.setState({errorForm:validate.errors})
            return false
        } else {
            this.setState({errorForm:[]})
            return true;
        }
    }

    handleSubmit(e) {
        e.preventDefault()
        if (this.isValidUser()) {

            let user = {
                name:this.state.name,
                username:this.state.username,
                password:this.state.password
            }
            console.log(`${baseUrlBackend}/register-user`)
            axios.post(`${baseUrlBackend}/register-user`,user).then(response =>{
                this.newToast("Usuário cadastrado com sucesso!")
                window.location.href = "/listar-usuarios"
            }).catch(err => {
                console.log(err.response.status)
                switch(err.response.status){
                    case 409: 
                        this.newToast("Usuário já possui cadastro!");
                    break
                    default:  
                          this.newToast("Ocorreu um erro com o servidor, tente novamente mais tarde.")
                }
            })
        } else {
            if (!this.state.password || !this.state.username || !this.state.email) {
                this.newToast("Todos os campos devem ser preenchidos.")
            } else {
                this.newToast("Existem campos inválidos.")
            }
        }

    }

    componentDidMount() {
        this.setState({isInfinite:true})
    }

    handleToggleCheck() {
       this.setState({
           isInfinite:!this.state.isInfinite
       })
    }

    addDays(date, days) {
        var result = new Date(date);
        result.setDate(result.getDate() + days);
        return result;
    }

    messageError() {
        const { errorForm } = this.state
        if (errorForm.length > 0) {
          return (<div className="errors">
                    {errorForm.map((error) => {
                        <li key={error}>-{error}</li>
                    })}
                 </div>)
        }
    }

    newToast = (message) => {
        this.setState({toastMessage:message}, () => {
            this.child.current.show()
        })
    }

    render() {

        const { formRegister, name, description, isInfinite, errorForm } = this.state

        const messError = this.messageError()

        return (

            <Fragment>
                <Main {...headerProps}>
                <Toast message={this.state.toastMessage} time={3000} ref={this.child}/>
                    <form>
                        {messError}
                        <div className="form-control">
                            <label>{formRegister.name.label}</label>
                            <input id={formRegister.name.inputName} name={formRegister.name.inputName} type="text" value={name} maxLength={formRegister.name.maxLength} />
                        </div>
                        <div className="formRegister-control">
                            <label>{formRegister.description.description}</label>
                            <textarea name={formRegister.description.inputName} id={formRegister.description.inputName} rows="20" cols="50" maxlength={formRegister.description.maxLength}>{description}</textarea>
                        </div>
                        <div className="formRegister-control">
                            <label>{formRegister.infinite.label}</label>
                            <input onChange={this.handleToggleCheck} defaultChecked={isInfinite} id={formRegister.infinite.inputName} name={formRegister.infinite.inputName} type="checkbox" />
                        </div>
                         {isInfinite && dateInterval}
                        <div className="d-flex  flex-row-reverse">
                            <button onClick={e=>this.handleSubmit(e)}>{formRegister.submitButton}</button>
                        </div>
                    </form>
                </Main>
            </Fragment>
        )
    }
}
