import './TaskRegister.scss'
import React, {Component, Fragment, createRef} from 'react'
import Main from '../templates/Main'
import axios from 'axios'
import Toast from '../utils/Toast';
import baseUrl from '../../config/baseUrl'

const baseUrlBackend = `${baseUrl.baseUrlBackend}`

const headerProps = {
    icon: 'sign-in',
    title: 'Registrar',
    subtitle: 'Você precisa estar logado para acessar o sistema.',
}
const currentDate = new Date()

export default class TaskRegister extends Component {
    constructor(props) {
        super(props)

        this.child = createRef()

        this.state = {
            name: props.name || "",
            description: props.description || "",
            startDate: props.startDate || currentDate,
            endDate: props.endDate || this.addDays(currentDate, 10),
            isInfinite: props.isInfinite || false,
            toastMessage:"false",
            formRegister:{
                name:{
                    label:"Título da tarefa: ",
                    inputName:"task",
                    maxLength:45
                },
                description:{
                    label:"Descrição da tarefa: ",
                    inputName:"description",
                    maxLength:1000
                },
                infinite:{
                    label: "Infinito",
                    inputName:"infinite"
                },
                interval:{
                    startDate:{
                        label:"Data inicial",
                        inputName:"start_date"
                    },
                    endDate:{
                        label: "Data final",
                        inputName: "end_date"
                    }
                },
                permissionSubit:false,
                submitButton:"Cadastrar"
            }
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleToggleCheck = this.handleToggleCheck.bind(this)
        this.handleTaskNameChange = this.handleTaskNameChange.bind(this)
        this.handleTaskDescriptionChange = this.handleTaskDescriptionChange.bind(this)
        this.handleStartDateChange = this.handleStartDateChange.bind(this)
        this.handleEndDateChange = this.handleEndDateChange.bind(this)

    }

    handleSubmit(e) {
        e.preventDefault()
        if(this.state.name && this.state.name !== '') {

            if(this.state.isInfinite === false) {
                if(!this.state.startDate) {
                    this.newToast("A data inicial precisa ser registrada.")
                    this.setState({
                        permissionSubit:false
                    })
                }
                if(!this.state.endDate) {
                    this.newToast("A data final precisa ser registrada.")
                    this.setState({
                        permissionSubit:false
                    })
                }
                if(this.state.startDate && this.state.startDate) {
                    if(new Date(this.state.startDate).getTime() > new Date(this.state.endDate).getTime()) {
                        this.newToast("A data final precisa ser maior que a data inicial.")
                        this.setState({
                            permissionSubit:false
                        })
                    }
                }
            }
            if(this.state.permissionSubit) {
                    let task = {
                        name:this.state.name,
                        description:this.state.description,
                        isInfinite:this.state.isInfinite,
                        endDate:this.state.startDate,
                        startDate:this.state.endDate

                    }
                    console.log(`${baseUrlBackend}/register-task`)
                    axios.post(`${baseUrlBackend}/register-task`,task).then(resp =>{
                        localStorage.setItem('SessionToken', resp.data.token)
                        this.newToast("Tarefa cadastrada com sucesso!")
                        window.location.href = "/home"
                    }).catch(err => {
                        console.log(err.response.status)
                        switch(err.response.status){
                            case 409: 
                            this.newToast("Tarefa já cadastrada!");
                            break
                            default:  
                            this.newToast("Ocorreu um erro com o servidor, tente novamente mais tarde.")
                        }
                    })
            }
        } else {
            if (!this.state.name) {
                this.newToast("Preencha o nome da tarefa.")
            }else{
                this.newToast("Existem campos inválidos.")
            }
        }

    }

    componentDidMount() {
        this.setState({
            isInfinite:true
        })
    }

    handleToggleCheck() {
       this.setState({
           isInfinite:!this.state.isInfinite
       })
    }

    handleTaskNameChange(event) {
        this.setState({name: event.target.value});
    }

    handleTaskDescriptionChange(event) {
        this.setState({description: event.target.value});
    }

    handleStartDateChange(event) {
        this.setState({startDate: event.target.value});
    }
    handleEndDateChange(event) {
        this.setState({endDate: event.target.value});
    }

    addDays(date, days) {
        var result = new Date(date);
        result.setDate(result.getDate() + days);
        return result;
    }

    newToast = (message) => {
        this.setState({toastMessage:message}, () => {
            this.child.current.show()
        })

    }

    formInterval() {
        const { formRegister, startDate, endDate } = this.state
            return  (<div>
            <div className="form-control">
            <label>{formRegister.interval.startDate.label}</label>
                    <input type="datetime-local" onChange={this.handleStartDateChange} name={formRegister.interval.startDate.inputName} id={formRegister.interval.startDate.inputName} value={startDate} />
            </div>
            <div className="form-control">
            <label>{formRegister.interval.endDate.label}</label>
                    <input type="datetime-local" onChange={this.handleEndDateChange} name={formRegister.interval.endDate.inputName} id={formRegister.interval.endDate.inputName} value={endDate} />
            </div>
    </div>)

    }

    render() {

        const { formRegister, isInfinite, name, description } = this.state

        const dateInterval = this.formInterval()

        return (

            <Fragment>
                <Main {...headerProps}>
                <Toast message={this.state.toastMessage} time={3000} ref={this.child}/>
                    <form>
                        <div className="form-control">
                            <label>{formRegister.name.label}</label>
                            <input id={formRegister.name.inputName} name={formRegister.name.inputName} onChange={this.handleTaskNameChange} type="text" value={name} maxLength={formRegister.name.maxLength} />
                        </div>
                        <div className="form-control">
                            <label>{formRegister.description.description}</label>
                            <textarea name={formRegister.description.inputName} onChange={this.handleTaskDescriptionChange} id={formRegister.description.inputName} rows="20" cols="50" maxlength={formRegister.description.maxLength}>{description}</textarea>
                        </div>
                        <div className="form-control">
                            <label>{formRegister.infinite.label}</label>
                            <input onChange={this.handleToggleCheck} value={isInfinite} id={formRegister.infinite.inputName} name={formRegister.infinite.inputName} type="checkbox" />
                        </div>
                         {isInfinite && dateInterval}
                        <div className="d-flex  flex-row-reverse">
                            <button onClick={e=>this.handleSubmit(e)}>{formRegister.submitButton}</button>
                        </div>
                    </form>
                </Main>
            </Fragment>
        )
    }
}
