import './ListTasks.scss'
import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import Main from '../templates/Main'

const ListTasks = props => {

    const list = props.taskLists || []

    const headerProps = {
        icon: 'tasks',
        title: 'Tarefas',
        subtitle: 'Lista de tarefas cadastradas.',
    }

    const [ listTasks ] = useState(list)

    if(listTasks.length > 0) {
        return (
            <Main {...headerProps}>
                <div className="content-list-tasks">
                    <ul>
                        {listTasks.map((task) => {
                        return <li key={task.id}>{task.id} - <Link to={`/${task.id}/ver-tarefa`}>{task.name}</Link> | <Link to={`/${task.id}/editar-tarefa`}>Editar tarefa</Link></li>;
                    })}
                    </ul>
                </div>
            </Main>
        )
    } else {
        return (<Main  {...headerProps}><div className="content-list-users"><h3>NÃ£o hÃ¡ tarefas para listar.</h3></div></Main>)
    }

}
export default ListTasks
