import 'font-awesome/css/font-awesome.min.css'
import './TaskApp.scss'
import React, {Component} from 'react'
import {BrowserRouter} from 'react-router-dom'
//      {HashRouter} faz a mesma navegação usando # 

import Routes from './Routes'
import Logo from '../components/templates/Logo'
import Footer from '../components/templates/Footer'
import Nav from '../components/templates/Nav'
import Overlay from '../components/templates/Overlay'
import axios from 'axios'
import Toast from '../components/utils/Toast'
import baseUrl from '../config/baseUrl'

const baseUrlBackend = `${baseUrl.baseUrlBackend}`

export default class TaskApp extends Component {
    constructor(props){
        super(props)
        this.state = {
            // connection: "Disconnected",
            // connectionDescription: "Connecting...",
            // setConnection: this.setConnection.bind(this),
            // nextConnectionTry: 0,
            // authenticated: false,

            isFinite:true,
            listTasks:[],
            listUsers:[]
        }
    }
    setConnection(connection, connectionDescription){ //If connection is !== of Connected, overlay pop up (Check overlay component)
        this.setState({connection, connectionDescription})
    }

    newToast = (message) => {
        this.setState({toastMessage:message})
        this.child.current.show();
    };

    componentDidMount(){
        // let token = localStorage.getItem('SessionToken')
        // var headers = {
        //     'Authorization': `bearer ${token}`
        // }
        var headers = {    
            "Access-Control-Allow-Origin" : `${baseUrlBackend}`,
            "Access-Control-Allow-Headers": "*", 
            //"Access-Control-Allow-Methods": "GET, POST, OPTIONS, PUT, PATCH, DELETE" ,
            "Access-Control-Allow-Methods": "GET, POST" ,
            "Access-Control-Max-Age": "86400",
            "Content-Type": "application/json;charset=UTF-8"                   
           }

        let params = {
            all:true
        }
        axios.post(`${baseUrlBackend}/list/task`, params, headers)
        .then(response => {
            this.setState({
                listTasks:response.data.listTasks
            })

        }).catch(err => {

            if(err.response){
               if (err.response.status === 401){
                  this.newToast('Problemas na requisição')
               } else {
                  this.newToast('Ocorreu um erro de conexão') 
               }
            }
                //             console.log('a')
                //             this.setState( {connection:"Connected", connectionDescription:"Connected", authenticated:false} )//Connected but not authenticated
                //         }else{
                //             console.log('b')
                //             this.setState({nextConnectionTry: 10, connection:"Disconnected", connectionDescription:"Server offline"})//not cnonnected
                //         }
                //     }else
                //     this.setState({nextConnectionTry: 10, connection:"Disconnected", connectionDescription:"Server offline"})//Server offline
                // })

        })

        axios.post(`${baseUrlBackend}/list/user`, params, headers)
        .then(response => {

            this.setState({
                listUsers:response.data.listUsers
            })
        }).catch(err => {

            if(err.response){
               if (err.response.status === 401){
                  this.newToast('Problemas na requisição')
               } else {
                  this.newToast('Ocorreu um erro de conexão') 
               }
            }
                //             console.log('a')
                //             this.setState( {connection:"Connected", connectionDescription:"Connected", authenticated:false} )//Connected but not authenticated
                //         }else{
                //             console.log('b')
                //             this.setState({nextConnectionTry: 10, connection:"Disconnected", connectionDescription:"Server offline"})//not cnonnected
                //         }
                //     }else
                //     this.setState({nextConnectionTry: 10, connection:"Disconnected", connectionDescription:"Server offline"})//Server offline
                // })

        })
        // axios.post(`${baseUrlBackend}auth`,'', {headers: headers} ).then(response => {//pass token as header
        //     console.log(response)
        //     this.setState( {connection:"Connected", connectionDescription:"Connected", authenticated: true} )//Connected AND authenticated
        // }).catch(err => {
        //     console.log(err)
        //     if(err.response){
        //         if(err.response.status === 401){
        //             console.log('a')
        //             this.setState( {connection:"Connected", connectionDescription:"Connected", authenticated:false} )//Connected but not authenticated
        //         }else{
        //             console.log('b')
        //             this.setState({nextConnectionTry: 10, connection:"Disconnected", connectionDescription:"Server offline"})//not cnonnected
        //         }
        //     }else
        //     this.setState({nextConnectionTry: 10, connection:"Disconnected", connectionDescription:"Server offline"})//Server offline
        // })
    }

    render(){
        return(
        <BrowserRouter>
            <div className="task-app">
                <Overlay {...this.state}/>
                <Logo />
                <Nav {...this.state}/>
                <Routes {...this.state}/>
                <Footer />
            </div>
        </BrowserRouter>
        )
    }
}
