import React from 'react'
import {Switch, Route, Redirect} from 'react-router'

import Home from '../components/home/Home'
import CrudTests from '../components/user/CrudTests'
// import Login from '../components/login/Login'
import Register from '../components/register/Register'
import WithAuth from '../components/utils/WithAuth'



export default props => {
    let myProps = props
    let ProtectedTestsPage = WithAuth(CrudTests)

    const loginProps = {
        input1_placeholder:'Digite seu usuário',
        input2_placeholder:'Digite sua senha',
        input1_label:'Usuário: ',  
        input2_label:'Senha: ',                              //Label of this form
        input1_textHelper:'Preencha o usuário corretamente.',        //Default text message
        input2_textHelper: 'Preencha a senha corretamente.',     
        input1_validation: [{
                 validationType:"password",
                 textWhenInvalid:"Digite uma senha válida"},
             {
             validationType:"length",
             args:{ min:4, max: 10 },
             textWhenInvalid:"O usuário deve ter pelo menos 4 caracteres e o máximo de 10 caracteres."}
         ],
        input2_validation: [{
                     validationType:"length",
                     textWhenInvalid:"Sua senha deve ter pelo menos 8 caracteres.",
                     args:{ min:8, max: 150 }
        }]

     }

    //Outras formas de fazer o link
    //<Route exact path='/' component={Home}/>
    //<Route path='/users' render={props => <UserCrud {...myProps} />}/>
    return(
    <Switch>
        {/* <Route path='/home' render={props => <Home {...myProps} />}/> */}


        <Route
            path='/users'
            render={
                (props) =>  <ProtectedTestsPage {...myProps}/>
            }
        />


        <Route exact path='/' render={props => <Home {...loginProps} />}/>
        <Redirect from='*' to='/'/>
    </Switch>
    )
}
