import './TaskRegister.scss'
import React, {Component, Fragment, createRef} from 'react'
import Main from '../templates/Main'
import axios from 'axios'
import Toast from '../utils/Toast';
import baseUrl from '../../config/baseUrl'

const baseUrlBackend = `${baseUrl.baseUrlBackend}`

const headerProps = {
    icon: 'sign-in',
    title: 'Registrar',
    subtitle: 'Você precisa estar logado para acessar o sistema.',
}
const currentDate = new Date().toLocaleString()

export default class TaskRegister extends Component {
    constructor(props) {
        super(props)

        this.child = createRef()


        this.state = {
            name: props.name || "",
            description: props.description || "",
            startDate: props.startDate || currentDate,
            endDate: props.endDate || this.addDays(currentDate, 10),
            isInfinite: props.isInfinite || true,
            toastMessage:"false",
            form:{
                name:{
                    label:"Título da tarefa",
                    inputName:"task",
                    maxLength:45
                },
                description:{
                    label:"Descrição da tarefa",
                    inputName:"description",
                    maxLength:1000
                },
                infinite:{
                    label: "Infinito",
                    inputName:"infinite"
                },
                interval:{
                    startDate:{
                        label:"Data inicial",
                        inputName:"start_date"
                    },
                    endDate:{
                        label: "Data final",
                        inputName: "end_date"
                    }
                },
                submitButton:"Cadastrar"

            }
        }
    }

    handleSubmit(e) {
        e.preventDefault()
        if(this.state.name && this.state.name !== '') {

            if(!this.state.isInfinite) {
                if(!this.state.startDate) {
                    this.newToast("A data inicial precisa ser registrada.")
                }
                if(!this.state.endDate) {
                    this.newToast("A data final precisa ser registrada.")
                }
                if(this.state.startDate && this.state.startDate) {
                    if(new Date(this.state.startDate).getTime() > new Date(this.state.endDate).getTime()) {
                        this.newToast("A data final precisa ser maior que a data inicial.")
                    }
                }
            }

            let task = {
                name:this.state.name,
                descriṕtion:this.state.description,
                isInfinite:this.state.isInfinite,
                endDate:this.state.startDate,
                startDate:this.state.endDate

            }
            console.log(`${baseUrlBackend}/register-task`)
            axios.post(`${baseUrlBackend}/register-task`,task).then(resp =>{
                localStorage.setItem('SessionToken', resp.data.token)
                this.newToast("Tarefa cadastrada com sucesso!")
                window.location.href = "/home"
            }).catch(err => {
                console.log(err.response.status)
                switch(err.response.status){
                    case 409: this.newToast("Tarefa já cadastrada!");break
                    default:  this.newToast("Ocorreu um erro com o servidor, tente novamente mais tarde.")
                }
            })
        }else{
            if(!this.state.password || !this.state.username || !this.state.email){
                this.newToast("Todos os campos devem ser preenchidos.")
            }else{
                this.newToast("Existem campos inválidos.")
            }
        }

    }

    componentDidMount() {
        this.setState({isInfinite:true})
        console.log('startdate',this.state.startDate)
    }

    handleToggleCheck() {
       this.setState({
           isInfinite:!this.state.isInfinite
       })
    }

    addDays(date, days) {
        var result = new Date(date).toLocaleString();
        result.setDate(result.getDate() + days);
        return result;
    }

    newToast = (message) => {
        this.setState({toastMessage:message}, () => {
            this.child.current.show()
        })

    }

    formInterval() {
        const { form } = this.state
            return  (<div>
            <div className="form-control">
            <label>{form.interval.startDate.label}</label>
                    <input type="datetime-local" name={form.interval.startDate.inputName} id={form.interval.startDate.inputName} value={this.state.startDate} />
            </div>
            <div className="form-control">
            <label>{form.interval.endDate.label}</label>
                    <input type="datetime-local" name={form.interval.endDate.inputName} id={form.interval.endDate.inputName} value={this.state.endDate} />
            </div>
    </div>)

    }

    render() {

        const { form,   isInfinite } = this.state
        const { name, description } = this.props

        const dateInterval = this.formInterval()

        return (

            <Fragment>
                <Main {...headerProps}>
                <Toast message={this.state.toastMessage} time={3000} ref={this.child}/>
                    <form>
                        <div className="form-control">
                            <label>{form.name.label}</label>
                            <input id={form.name.inputName} name={form.name.inputName} type="text" value={this.props.name} maxLength={form.name.maxLength} />
                        </div>
                        <div className="form-control">
                            <label>{form.description.description}</label>
                            <textarea name={form.description.inputName} id={form.description.inputName} rows="20" cols="50" maxlength={form.description.maxLength}>{description}</textarea>
                        </div>
                        <div className="form-control">
                            <label>{form.infinite.label}</label>
                            <input onChange={this.handleToggleCheck} defaultChecked={isInfinite} id={form.infinite.inputName} name={form.infinite.inputName} type="checkbox" />
                        </div>
                         {isInfinite && dateInterval}
                        <div className="d-flex  flex-row-reverse">
                            <button onClick={e=>this.handleSubmit(e)}>{form.submitButton}</button>
                        </div>
                    </form>
                </Main>
            </Fragment>
        )
    }
}
