import './ListUsers.scss'
import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import Main from '../templates/Main'

const ListUsers = props => {

    const list = props.userList || []

    const headerProps = {
        icon: 'users',
        title: 'Usuários',
        subtitle: 'Lista de usuários cadastrados.',
    }

    const [ listUser ] = useState(list)

    if(listUser.length > 0) {
        return (
            <Main {...headerProps}>
            <div className="content-list-users">
                <ul>
                {listUser.map((user) => {
                    return <li key={user.id}>{user.id} - <Link to={`/${user.id}/editar-usuario`}>Editar usuário</Link></li>;
                })}
                </ul>
            </div>
            </Main>
        )
    } else {
        return (<Main {...headerProps}><div className="content-list-users"><h3>Não há usuários para listar.</h3></div></Main>)
    }

}
export default ListUsers
